// 获取网页不含域名的路径
var windowPath=window.location.pathname,FriendDataPath="/friends/friendslist.json";
// 友链信息文件路径
// 链接卡片（友链）
if(windowPath.indexOf("friends")>0){var LinkDataPath=FriendDataPath;link={init:function(){var a=this;$.getJSON(LinkDataPath,(function(i){a.render(i)}))},render:function(a){for(var i,n,t,d="",r=0;r<a.length;r++)i=a[r].name,n=a[r].avatar,d+='<div class="card"><a href="'+(t=a[r].link)+'" target="_blank"><div class="thumb" style="background: url('+n+');"></div></a><div class="card-header"><div><a href="'+t+'" target="_blank">'+i+'</a></div><div><a href="'+t+'" target="_blank">'+a[r].description+"</a></div></div></div>";$(".MyGrid").append(d)}},link.init()}