var windowPath = window.location.pathname;
// 友链信息文件路径
var FriendDataPath = '/friends/friendslist.json';
// 链接卡片（友链）
if (windowPath.indexOf('friends') > 0) {
    var LinkDataPath = FriendDataPath;
    link = {
        init: function () {
            var that = this;
            $.getJSON(LinkDataPath, function (data) {
                that.render(data);
            });
        },
        render: function (data) {
            var name, avatar, link, description, li = "";
            for (var i = 0; i < data.length; i++) {
                name = data[i].name;
                avatar = data[i].avatar;
                link = data[i].link;
                description = data[i].description;
                li += '<div class="card">' +
                    '<a href="' + link + '" target="_blank">' +
                    '<div class="thumb" style="background: url(' + avatar + ');">' + '</div>' +
                    '</a>' +
                    '<div class="card-header">' +
                    '<div><a href="' + link + '" target="_blank">' + name + '</a></div>' +
                    '<div><a href="' + link + '" target="_blank">' + description + '</a></div>' +
                    '</div>' +
                    '</div>';
            }
            $(".MyGrid").append(li);
        }
    }
    link.init();
}

