    let opacityValue = 1;

    function animate() {
      top--;
      opacityValue = opacityValue - 0.02;
      $span.style.top = top + "px";
      $span.style.opacity = opacityValue;
      const newTime = new Date().getTime();
      const diff = newTime - initTime;
      if (diff < 600) {
        window.requestAnimationFrame(animate);
      } else {
        $span.remove();
      }
    }
    window.requestAnimationFrame(animate);
